/**
 * Get the Dutch description for a GravelType code.
 *
 * @param code - The code value
 * @returns The Dutch description, or undefined if the code is not recognized
 */
export function getBhrgGravelTypeDescription(code: string): string | undefined {
  return BHRG_GRAVEL_TYPE_CODES[code];
}
